// Función para formatear la fecha y hora
function formatDateTime(timestamp) {
    var options = {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false,
        timeZone: 'America/Santiago'
    };
    var formattedDateTime = timestamp.toLocaleString('es-ES', options);
    return formattedDateTime;
}

// Obtener la fecha actual y formatearla
